script:
frappe.ui.form.on("Sales Order",{
    onload(frm){set_price_list(frm);},
    customer(frm){set_price_list(frm);}  
});
function set_price_list(frm){
    if(!frm.doc.customer)return;
    frappe.db.get_value("Customer",frm.doc.customer,"customer_channel")
        .then(r=>{
            const ch=r.message.customer_channel;
            const pl=ch==="Wholesale"?"Coffee Wholesale - ETB":"Coffee Retail - ETB";
            if(frm.doc.selling_price_list!==pl){frm.set_value("selling_price_list",pl);}
        });
}
