frappe.ui.form.on('Green Bean Assessment', {
    validate: function(frm) {
        if (!frm.doc.sample_id) {
            const now = frappe.datetime.now_date();  // YYYY-MM-DD
            const mm = now.split("-")[1];
            const yy = now.split("-")[0].slice(-2);
            const prefix = mm + yy;

            frappe.call({
                method: "frappe.client.get_list",
                args: {
                    doctype: "Green Bean Assessment",
                    filters: [["sample_id", "like", prefix + "%"]],
                    fields: ["name", "sample_id"],
                    limit: 1000
                },
                async: false, // this forces sync (important!)
                callback: function(r) {
                    let max = 0;
                    (r.message || []).forEach(doc => {
                        const num = parseInt(doc.sample_id?.slice(4)) || 0;
                        if (num > max) max = num;
                    });
                    const next = (max + 1).toString().padStart(5, "0");
                    frm.set_value("sample_id", prefix + next);
                }
            });
        }
    }
});

function calculate_all_totals(frm) {
    let total1 = 0;
    if (frm.doc.cat1_defects) {
        frm.doc.cat1_defects.forEach(d => {
            total1 += d.defect_count || 0;
        });
    }
    frm.set_value("total_cat1_defects", total1);

    let total2 = 0;
    if (frm.doc.cat2_defects) {
        frm.doc.cat2_defects.forEach(d => {
            total2 += d.defect_count || 0;
        });
    }
    frm.set_value("total_cat2_defects", total2);

    frm.set_value("total_defects", total1 + total2);
}

frappe.ui.form.on("Green Bean Assessment", {
    refresh(frm) {
        calculate_all_totals(frm);
    },
    cat1_defects_add(frm) {
        calculate_all_totals(frm);
    },
    cat1_defects_remove(frm) {
        calculate_all_totals(frm);
    },
    cat2_defects_add(frm) {
        calculate_all_totals(frm);
    },
    cat2_defects_remove(frm) {
        calculate_all_totals(frm);
    }
});

frappe.ui.form.on("Category 1 Defect Entry", {
    defect_count(frm) {
        calculate_all_totals(frm);
    }
});

frappe.ui.form.on("Category 2 Defect Entry", {
    defect_count(frm) {
        calculate_all_totals(frm);
    }
});

frappe.ui.form.on('Affective Cupping', {
  cup_scores_add: function(frm) {
    calculate_final_score(frm);
  },
  cup_scores_remove: function(frm) {
    calculate_final_score(frm);
  },
  // This triggers when the 'score' field of a row changes:
  cup_scores_score: function(frm, cdt, cdn) {
    calculate_final_score(frm);
  }
});

function calculate_final_score(frm) {
  let total = 0;
  let count = 0;

  (frm.doc.cup_scores || []).forEach(row => {
    if (row.score !== undefined && row.score !== null) {
      total += row.score;
      count += 1;
    }
  });

  let avg = count > 0 ? (total / count) : 0;

  frm.set_value('final_score', avg);
  frm.refresh_field('final_score');
}

frappe.ui.form.on('Physical Assessment', {
  // Recalculate anytime the form is loaded or any count field changes
  refresh(frm) {
    calculate(frm);
  },
  // You could also hook into each count field individually, but this blanket
  // listener is simpler and still performant on ~30 fields.
  validate(frm) {
    calculate(frm);
  }
});

function calculate(frm) {
  let total = 0.0;

  // Helper to parse ratio like "5 : 1" â†’ 5
  const parseRatio = (s) => {
    let parts = s.split(':');
    return parseFloat(parts[0]) || 1;
  };

  // List all count/defect pairs defined in your DocType JSON
  const pairs = [
    ['full_black_count','full_black_defects',          '1 : 1'],
    ['full_sour_count','full_sour_defects',            '1 : 1'],
    ['dried_cherry_count','dried_cherry_defects',      '1 : 1'],
    ['fungus_damage_count','fungus_damage_defects',    '1 : 1'],
    ['foreign_matter_count','foreign_matter_defects',  '1 : 1'],
    ['severe_insect_damage_count','severe_insect_damage_defects', '5 : 1'],

    ['partial_black_count','partial_black_defects',    '3 : 1'],
    ['partial_sour_count','partial_sour_defects',      '3 : 1'],
    ['parchment_count','parchment_defects',            '5 : 1'],
    ['floater_count','floater_defects',                '5 : 1'],
    ['immature_count','immature_defects',              '5 : 1'],
    ['withered_count','withered_defects',              '5 : 1'],
    ['shell_count','shell_defects',                    '5 : 1'],
    ['broken_chipped_count','broken_chipped_defects',  '5 : 1'],
    ['hull_husk_count','hull_husk_defects',            '5 : 1'],
    ['slight_insect_damage_count','slight_insect_damage_defects','10 : 1']
  ];

  // Compute perâ€pair and accumulate
  pairs.forEach(([cnt, fld, ratio]) => {
    let count = cint(frm.doc[cnt] || 0);
    let r = parseRatio(ratio);
    let full = (count / r) || 0;
    full = Math.round(full * 100) / 100;          // two decimals
    frm.set_value(fld, full);
    total += full;
  });

  // Set total
  frm.set_value('total_full_defects', total);

 // Determine grade
  let grade;
  if (total <= 3)        grade = '1';
 else if (total <= 12)  grade = '2';
else if (total <= 25)  grade = '3';
else if (total <= 45)  grade = '4';
 else if (total <= 100) grade = '5';
 else                   grade = '6-9';

 frm.set_value('grade', grade);
}
frappe.ui.form.on('Physical Assessment', {
    // this runs on both a brand-new form and after every save
    refresh(frm) {
        const show = true;   // always visible

        // call once per field you want to show / hide
        frm.toggle_display('sec_break_summary', show);   // the section label
        frm.toggle_display('max_screen_size',   show);   // the field inside
         // âœ… Total Score = 100 - total_full_defects
  const base_score = 100;
  const total_score = Math.max(base_score - total, 0);
  frm.set_value('total_score', total_score);
        // add more lines if you need other fields
    }
    });

/**  Physical Assessment â€“ screen-size metrics
 *   â€¢ % = bean_count / 300 * 100   (fixed denominator)
 *   â€¢ max_screen_size = screen # with the largest count
 *   Works with individual fields: screen10_count â€¦ screen23_count
 */

frappe.ui.form.on('Physical Assessment', (() => {
    // ------------------------------------------------------------------
    // config
    // ------------------------------------------------------------------
    const SAMPLE_SIZE = 350;               // fixed denominator
    const SCREENS     = Array.from({length: 14}, (_, i) => 10 + i);  // 10 â€¦ 23

    // ------------------------------------------------------------------
    // helper â€“ recalc everything
    // ------------------------------------------------------------------
    function update_screen_metrics(frm) {
        let maxCount = 0;
        let maxSize  = 0;

        SCREENS.forEach(size => {
            // read the *_count field (defaults to 0)
            let count = cint(frm.doc[`screen${size}_count`]) || 0;

            // restrict to 350g max
            if (count > 350) {
                frappe.msgprint(`Screen #${size} count cannot exceed 350g`);
                count = 350;
                frm.set_value(`screen${size}_count`, 350);
            }

            // set its % field
            const pct = (count * 100) / SAMPLE_SIZE;
            frm.set_value(`screen${size}_percent`, flt(pct, 2));

            // track max
            if (count > maxCount) {
                maxCount = count;
                maxSize  = size;
            }
        });

        // summary line: â€œ90 beans â€¢ 30.0 % â€¢ #17â€ (or â€œâ€“â€ if nothing entered)
        const summaryPct   = (maxCount * 100) / SAMPLE_SIZE;
        const summaryText  = maxCount
            ? `${maxCount} beans â€¢ ${flt(summaryPct, 1)} % â€¢ #${maxSize}`
            : 'â€“';

        frm.set_value('max_screen_size', maxSize || null);
    }

    // ------------------------------------------------------------------
    // event map
    // ------------------------------------------------------------------
    const events = {
        onload:  frm => {
            frm.set_df_property('sec_break_summary', 'hidden', 0);  // ðŸ‘ˆ new
            update_screen_metrics(frm);
        },
        refresh: update_screen_metrics,
        validate: update_screen_metrics
    };

    // attach a change handler for every *_count field
    SCREENS.forEach(size => {
        events[`screen${size}_count`] = update_screen_metrics;
    });

    return events;
})());

frappe.ui.form.on('Affective Assessment', {
  // any time one of these rating fields changesâ€¦
  fragrance_rating: compute_total,
  aroma_rating:      compute_total,
  flavor_rating:     compute_total,
  aftertaste_rating: compute_total,
  acidity_rating:    compute_total,
  sweetness_rating:  compute_total,
  mouthfeel_rating:  compute_total,
  overall_rating:    compute_total,
});

function compute_total(frm) {
  // list your rating fields
  const fields = [
    'fragrance_rating',
    'aroma_rating',
    'flavor_rating',
    'aftertaste_rating',
    'acidity_rating',
    'sweetness_rating',
    'mouthfeel_rating',
    'overall_rating'
  ];

  // sum up the integer values
  let sum = fields.reduce((acc, f) => {
    return acc + (parseInt(frm.doc[f]) || 0);
  }, 0);

  // set the total_score field
  frm.set_value('total_score', sum);
}

frappe.ui.form.on('Roasted Coffee', {
  refresh: function(frm) {
    if (!frm.is_new() && !frm.doc.stock_entry) {
      frm.add_custom_button(__('Create Stock Entry'), function() {
        frappe.call({
          method: 'coffee_roaster.api.create_stock_entry_from_roasted',
          args: { roasted_name: frm.doc.name },
          callback: function(r) {
            if (!r.exc) {
              frm.reload_doc();
            }
          }
        });
      }, __("Actions"));
    }
  }
});

function update_total_and_grade(frm) {
    const ATTR = [
        'fragrance_rating', 'aroma_rating', 'flavor_rating', 'aftertaste_rating',
        'acidity_rating', 'sweetness_rating', 'mouthfeel_rating', 'overall_rating'
    ];

    let sum = 0;
    let valid_scores = 0;

    ATTR.forEach(field => {
        const val = parseFloat(frm.doc[field]);
        if (!isNaN(val)) {
            sum += val;
            valid_scores++;
        }
    });

    // Correct total: max 80 (not multiplied)
    const cup = frappe.utils.flt(sum, 1);

    frm.set_value('total_score', cup);
    frm.refresh_field('total_score');

    let grade;
    if (cup >= 85)        grade = 1;
    else if (cup >= 80)   grade = 2;
    else if (cup >= 75)   grade = 3;
    else if (cup >= 70)   grade = 4;
    else if (cup >= 65)   grade = 5;
    else                  grade = 6;

    frm.set_value('grade', grade);
    frm.refresh_field('grade');
}

frappe.ui.form.on('Affective Assessment', {
    onload(frm) {
        update_total_and_grade(frm);
    },
    refresh(frm) {
        update_total_and_grade(frm);
    },
    fragrance_rating(frm)     { update_total_and_grade(frm); },
    aroma_rating(frm)         { update_total_and_grade(frm); },
    flavor_rating(frm)        { update_total_and_grade(frm); },
    aftertaste_rating(frm)    { update_total_and_grade(frm); },
    acidity_rating(frm)       { update_total_and_grade(frm); },
    sweetness_rating(frm)     { update_total_and_grade(frm); },
    mouthfeel_rating(frm)     { update_total_and_grade(frm); },
    overall_rating(frm)       { update_total_and_grade(frm); },
});

frappe.ui.form.on("Sales Order",{
    onload(frm){set_price_list(frm);},
    customer(frm){set_price_list(frm);}  
});
function set_price_list(frm){
    if(!frm.doc.customer)return;
    frappe.db.get_value("Customer",frm.doc.customer,"customer_channel")
        .then(r=>{
            const ch=r.message.customer_channel;
            const pl=ch==="Wholesale"?"Coffee Wholesale - ETB":"Coffee Retail - ETB";
            if(frm.doc.selling_price_list!==pl){frm.set_value("selling_price_list",pl);}
        });
}
