script:
# only run on Manufacture entries that reference a Roast Batch
if doc.purpose == "Manufacture" and doc.get("roast_batch"):
    for row in doc.items:
        if row.batch_no and row.t_warehouse:
            frappe.db.set_value(
                "Roast Batch",
                doc.roast_batch,
                "batch_no",
                row.batch_no
            )
            break

for d in doc.items:
    if d.sales_order_item:
        batch = frappe.db.get_value("Sales Order Item", d.sales_order_item, "batch_no")
        if batch:
            d.batch_no = batch

for d in doc.items:
    if d.delivery_note_item:
        batch = frappe.db.get_value("Delivery Note Item", d.delivery_note_item, "batch_no")
        if batch:
            d.batch_no = batch
